//Also see: https://www.shadertoy.com/view/sdSyDW

void mainImage(out vec4 O, vec2 I)
{
    //Make sure the output is 0.0
    O -= O;
    //Initialize variables
    vec2 r = iResolution.xy, a, b;
    //Iterate through points
    for(int i; i<625;
    //Add projected, colored points to output
    O += r.x/3e3*(cos(a.y*1e3+vec4(0,1,2,0))+1.)/max(length(I-b*r/(b.y+12.)-a*r*.2-r*.5)-(b.y+9.)*r.x/1e3,1.))
        //2D point position (-4 to +4)
        b = vec2(i%25,i++/25)-10.-fract(iTime),
        //Heightmap
        a = texture(iChannel0, (b+iTime)/6e2+1.).gr;
}
