float sdfArc(float sa, float ea, float r, vec2 uv){
    float tau = 6.28318530718;
    //find coterminal angles
    sa = mod(mod(radians(sa),tau) + tau, tau);
    ea = mod(mod(radians(ea),tau) + tau, tau);
    float a = mod(mod(atan(uv.y,uv.x),tau) + tau, tau);
    //https://stackoverflow.com/questions/12234574/calculating-if-an-angle-is-between-two-angles
    if ((sa <= a && a <= ea && sa < ea) || ((sa <= a || a <= ea) && sa >= ea)){
        return abs(r - length(uv));
    }
    //get closest endpoint
    vec2 p1 = vec2(cos(sa), sin(sa))*r;
    vec2 p2 = vec2(cos(ea), sin(ea))*r;
    return min(distance(p1, uv), distance(p2, uv));
}

vec3 colorSdf(float d){//from https://www.shadertoy.com/view/tlSGzG
    vec3 col = vec3(1.0) - sign(d*2.0)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d*2.0));
	col *= 0.8 + 0.2*cos(140.0*d*2.0);
	return mix(col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d*2.0)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    vec3 col = colorSdf(sdfArc(iTime*20.0, iTime*40.0, 0.3, uv - vec2(0.8, 0.5)));

    fragColor = vec4(col,1.0);
}
