vec4 Color1 = vec4(0.0, 0.1, 0.0, 1.0);
vec4 Color2 = vec4(0.0, 0.6, 0.0, 1.0);

float Dither = 4.0;

//https://www.shadertoy.com/view/lscGDr
vec3 dithered(in vec3 color, in vec2 uv, in float dither)
{
    if (dither > 0.0)
    {
        const vec3 magic = vec3( 0.06711056, 0.00583715, 52.9829189 );
        float f = fract( magic.z * fract( dot( uv, magic.xy ) ) );
        color = clamp(color + (dither/255.0) * f - ((dither*0.5)/255.0), vec3(0.0), vec3(1.0));
    }
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = mix(Color1, Color2, uv.x);
    vec4 colorDithered = color;
	colorDithered.rgb = dithered(colorDithered.rgb, fragCoord, Dither);


    /* top part of image is with Dither and bottom is without*/
	fragColor = uv.y < 0.5 ? color : colorDithered;
}
