// Modified version of font loader from https://www.shadertoy.com/view/ltcXzs
void drawChar(inout vec3 color, in vec3 charColor, in vec2 uv, in vec2 pos, in vec2 size, in int char) {
    uv = (uv - pos) / size + 0.5;
    vec2 charUv = uv / 16.0;
    vec2 dfdx = dFdx(charUv);
    vec2 dfdy = dFdy(charUv);
    if (all(lessThan(abs(uv - 0.5), vec2(0.5)))) {
        float val = textureGrad(iChannel0, charUv + fract(vec2(char, 15 - char / 16) / 16.0), dfdx, dfdy).r;
        color = mix(color, charColor, val);
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Default shader
    vec2 uv = fragCoord / iResolution.xy;
    vec3 color = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0.0, 2.0, 4.0));
    fragColor = vec4(color, 1.0);

    // Correct aspect ratio
    uv.x *= iResolution.x / iResolution.y;

    // "Sound In" notification
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.3, 0.3), vec2(0.1), 83); // S
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.4, 0.3), vec2(0.1), 79); // O
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.5, 0.3), vec2(0.1), 85); // U
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.6, 0.3), vec2(0.1), 78); // N
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.7, 0.3), vec2(0.1), 68); // D

    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.9, 0.3), vec2(0.1), 73); // I
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(1.0, 0.3), vec2(0.1), 78); // N
}

#define TAU 6.2831853072

float whiteNoise(in float time) {
    return fract(495.381 * cos(937.385 * time + 18.387));
}

float squareWave(in float time) {
    return 1.0 - 2.0 * mod(floor(2.0 * time), 2.0);
}

float triWave(in float time) {
    return 1.0 - abs(mod(4.0 * time + 1.0, 4.0) - 2.0);
}

float sineWave(in float time) {
     return sin(TAU * fract(time));
}

float makeNoise(in float time) {
    //time = 10.0 * sin(2.0 * time + sin(10.0 * time));
    time = 5.0 * sin(time + sin(3.0 * time));
    time = mod(time, 0.5);
    float env = 0.0;
    float envTime = 50.0 * time;
    if (envTime < 1.0) env = 2.0 * envTime;
    else if (envTime < 2.0) env = 3.0 - envTime;
    else if (envTime < 4.0) env = 1.0;
    else if (envTime < 5.0) env = 5.0 - envTime;
    return sineWave(200.0 * time) * env * 0.5;// * 0.01;
}

vec2 mainSound(int samp, float time) {
    return vec2(makeNoise(time));
}
