const int KEY_SP = 32;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = texture(iChannel0,fragCoord/iResolution.xy).xy;

    if(texelFetch(iChannel3, ivec2(KEY_SP,0), 0 ).x > 0.){
    fragColor = texture(iChannel0,fragCoord/iResolution.xy);
    fragColor.ba = vec2(0.0);
    return;
    }
    fragColor = texture(iChannel1,uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    if(iFrame<1){
        fragColor = (fragCoord/iResolution.xy).xyxy; 
        return;
    }
    if(fragCoord.x<0.9 && fragCoord.y<0.9){
        fragColor = vec4(iMouse.xy, 0., 1.);
        return;
    }

    vec3 col = texture(iChannel0, uv).rgb;

    vec2 curr = iMouse.xy;
    vec2 prev = iMouse.w>0. ? iMouse.xy : texture(iChannel0,vec2(0)).xy;
    float lineL = length(curr - prev);
    float lineW = 50.;
    float press = 0.5;
    float brs = press * smoothstep(lineW,0., length(curr-fragCoord));

    col *=0.1;
    //col = vec3(0.);
    if(iMouse.z>0.){
        for(float u=0.0; u<lineL; u++){
            float pct = u / lineL;
            col += press * smoothstep(lineW, 0., length(mix(prev,curr,pct) - fragCoord)); 
        } 

        //fragCoord -= col.rg * (curr-prev).xy;
        fragCoord -= brs * (curr-prev).xy;

    }

    fragColor = texture(iChannel0, fragCoord  / iResolution.xy );

    //fragColor = vec4(col,1.0);
}
