vec4 number1(vec3 color)
{
    vec3 aColor = color;
    float mean = ((aColor.r+aColor.g+aColor.b)/3.0);
    aColor = vec3(mean,mean,mean);
    return vec4(aColor,1.0);
}

vec4 number2(vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 left = texture(iChannel0, uv);
    vec4 right = texture(iChannel1, uv);
    if(uv.x < .5)
        return left;
    return right;
}

vec4 number3(vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 image = texture(iChannel0, uv);
    image *= vec4(1.1,1,1,1.0);
    return image;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(.2,.5,.6);
    //fragColor = number1(color);
    fragColor = number2(fragCoord);
    //fragColor = number3(fragCoord);
}
