#define fmod1(x,y) fmod((x)+pow(2.,fmod((-x)*16.,2.)),y)
vec2 mainSound(int samp, float t){
  float tempo = 4.;
  t /= tempo;
  float
  tempo1 =
      4.
  ,
  m1 =
      4.
  ,
  c1 = fmod(t*tempo1,m1+fmod1(t,m1)),
  s1 = 1.+c1,
  t1 = t*tempo1,
  m2 = fmod(t1*(1.+fmod1(t,2.))/2.,s1),
  a=
      pow((1.-sqrt(fract((t*tempo1+m2+t1)/2.))/1.25),2.)*.2
  ,
  nb =
      t*tempo*pow(2.,m2/5.+7.5)/(1.+fmod1(t+m2,2.))
  ;
  return
      vec2(fract(nb*.998*2.),fract(nb))*a
  ;
