mat2 rot (float a) { float c=cos(a),s=sin(a); return mat2(c,-s,s,c); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float t = iTime * .2;

    vec3 col = vec3(0);

    vec3 ro = vec3(0,0,-1.2);
    vec3 lookat = vec3(0);

    vec3 fw = normalize(lookat-ro),
        r = normalize(cross(vec3(0,1,0),fw)),
        up = cross(fw,r),
        c = ro + fw*1.,
        i = c + uv.x * r + uv.y * up,
        rd = normalize(i-ro);

    float ds, d0;
    vec3 p;

    vec3 center = vec3(0);
    float radius = .5;
    vec3 normal = vec3(0);

    for(int i=0;i<100;i++) {
        p = ro + rd * d0;        
        p.xz*=rot(t);

        vec3 crd = (p-center);
        float noise = texture(iChannel0, crd).r * mix(.01, .042, sin(t*2.1)*.5+.5);
        radius = mix(.48, .5, (sin(p.y*p.x*mix(50.,200.,sin(t*3.)*.5+.5)*.5+.5)));
        ds = length(p-center) - radius - noise;        
        if(ds<0.001) break;
        d0 += ds;
    }

    //col.rb += sin(uv*100.);

    if(ds<0.001){
        normal = normalize(p+.5);
        normal = fwidth(normal)*100.;
        col = normal;
    }

    fragColor = vec4(col,1.0);
}
