void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    // Center our coordinate system    
    vec2 ouv = fragCoord/iResolution.xy;
    vec2 uv = (fragCoord - .5*iResolution.xy) / min(iResolution.x, iResolution.y);    
    vec2 uv2 = clamp(uv + vec2(0.5, 0.5),vec2(0.0), vec2(1.0));

    vec4 bgColor = texture(iChannel0, ouv);
    vec4 fgColor = texture(iChannel1, uv2);
    fragColor = mix(bgColor,fgColor, fgColor.a);
}
