float Mandelbrot(vec2 z){
    float radius = 180.;
    float speed=.5;
    vec2 pos = vec2(iResolution.x*.6,iResolution.y*.5) + vec2(cos(iTime*speed),sin(iTime*speed))*radius;
    vec2 screenPos = iMouse.xy;
    vec2 c;
    c = (screenPos/iResolution.xy - vec2(.5,.5))*3.;
    int iterations = 100;

    for(int i = 0; i < iterations; i++){
        float a = z.x;
        float b = z.y;
        z = vec2(a*a-b*b, 2.*a*b) + c;
        if(dot(z,z)>4.){
            return float(i)/float(iterations);
        }
    }
    return 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float v = Mandelbrot((fragCoord - iResolution.xy/2.)*3./iResolution.y);
    fragColor = vec4(0.5 + 0.5*cos(v*500.+vec3(0,2,4)+iTime/2.),0.);
}
