#define hash(p)  fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u+u - R ) / R.y;

#define H(s) vec4( hash( s+ U+iTime ) > length(U) )
    O =   U.y > 0. ?   H()
        : U.x > 0. ? ( H(.1)+H(.2)+H(.3)+H(.4)+H(.5)+H(.6)+H(.7)+H(.8) ) / 8.
                   :   vec4(1. - length(U));
    O = sqrt(O); // to sRGB
}
