void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 uv = fragCoord;

    float t = 1.0;
    float a = .5 + .5*cos(iTime*3. + uv.x - (uv.x*floor(uv.x/3.0)) + uv.y - (uv.y*floor(uv.y/3.0)));
    float b1 = (iTime-floor(iTime));
    float b = a; // + b1;
    //(uv.x*floor(uv.x / 7.0)) 
    float h = b - (b * floor(b/t));
    float h2 = exp(h)/2.71;

    vec3 col = vec3(1.-h2*2., (.5-h2)*(.5-h), h2*(abs(.25-h)+1.));
    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    // Output to screen
    fragColor = vec4(col,1.0);
}
