#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define cc(x, p) sign(x) * pow(abs(x), p)

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) - k * h * (1. - h);
}

float smax(float a, float b, float k) {
    float h = clamp(0.5 - 0.5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) + k * h * (1. - h); 
}

vec3 pal(in float t, in vec3 d) {
    return 0.5 + 0.5 * cos(2. * pi * (0.5 * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 ouv = uv;


    float sc = 12.;
    uv *= sc;
    uv.x -= iTime;

    //uv.y += cc(cos(0.02 * uv.x + 0.25 * iTime), 40.);
    float fx = fract(uv.x) - 0.5;
    float ix = floor(uv.x) + 0.5;
    uv.y += 0.2 * sc * thc(1.5, 0.1 * mix(ix, uv.x, 0.5 + 0.5 * cos(12. * uv.y)) + 0.5 * iTime);
    float m = 2. * mod(ix + 0.5, 2.) - 1.;
    float h = mix(1., 3., 0.5 + 0.5 * cos(0.2 * ix + iTime));
    float fy = uv.y + h * m * cc(thc(6., m * pi * 0.25 + 0.1 * ix + iTime), 0.25);

    float d = length(vec2(fx, fy)) - 0.5;

    float k = 0.75 * sc / iResolution.y;
    float s = smoothstep(-k, k, -fy);

    if (uv.y < 0.) {
        s = 1. - s;
    }

    s -= smoothstep(-k, k, -d + 0.075);
    s = max(s, smoothstep(-k, k, -d));
    k *= 15.;
    vec3 col1 = pal(0.25 * thc(1., ix) + 1.25 - abs(fy) / sc + 1. * abs(uv.y) / sc, 0.28* vec3(0,1,2)/3.);
    vec3 col2 = pal(0.25 * (2. * smoothstep(-k, k, uv.y) - 1.) + cos(0.25 * iTime + 0.5 * ouv.x) + ouv.y * 0.3, 0.45 * vec3(0,1,2)/3.);
    vec3 col = mix(col2, mix(col1, col2, exp(-0.75 * abs(uv.y))), s);
    col = mix(1.-vec3(0.,0.15,0.15), col, 0.8);
    fragColor = vec4(col,1.0);
}
