#define PI 3.14159265359

float rand (vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))*43758.5453123);
}

vec2 rand2 (vec2 st) {
    return vec2(fract(sin(dot(st.xy, vec2(32.9843,93.289)))*93769.4386302),
                fract(sin(dot(st.xy, vec2(48.5720,37.961)))*20450.7535901));
}

float box (vec2 uv, float o) {
    vec2 bl = step(vec2(o),uv);
    vec2 tr = step(vec2(o),1.0-uv);
    return bl.x * bl.y * tr.x * tr.y;
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float offUV (vec2 i, vec2 cell, vec2 cOff) {
    vec2 cCell = cell - cOff;
    vec2 off = rand2(cCell)*2.0-1.0;
    off *= rotate2d(iTime*1.5);
    off = off / 1.5;
    vec2 uv = i - off + cOff;
    return rand(cCell) * box(uv, 0.0);
}

