vec2 mainSound( in int samp, float time )
{
    return sound(time);
}

// http://countercomplex.blogspot.jp/2011/10/algorithmic-symphonies-from-one-line-of.html
// http://wurstcaptures.untergrund.net/music/

vec2 sound1( float time )
{
    int t = int(time);
    int t1 = t/1024;
    t = (t*(t1>>7)&t1)-1;
    //t = ((t*2&t/3))|(t&t/1024)-1;


    return vec2(float(t & 0xff - 128) / 128.);
}

vec2 sound(float time){
    float factor = 1.0;
    vec2 result = vec2(0.0);
    time += 7.;
    time *= 16000.;
    result += sound1(time*factor)/(factor);
    return result;
}
