// try messing with the values of period, amplitude, and cam_zoom

const float period = 1./2.; // period of wave over the course of a square
const float amplitude = 1. / period / 32.;
const float wavelength = 1. / period / radians(360.);
const float cam_zoom = 7.; // 7 stripes vertically: 4 white, 3 black

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float t = iTime * 5.;
    vec2 p = fragCoord.xy / iResolution.y * cam_zoom;
    p += sin(p.yx / wavelength + t) * amplitude;

    p = abs(mod(p,2.)-1.)-.5; // zig zag function
    p = smoothstep(1.5/iResolution.y*cam_zoom, 0., p); // free antialiazing
    fragColor = vec4(p.x * p.y);
}
