// Inspired by: https://www.shadertoy.com/view/XsVSzW

vec2 plasmaIter(vec2 uv, float freq, float phase, float amplitude, float orthAdd)
{
    vec2 v0 = sin(uv.yx * freq + phase) * amplitude;
    uv += v0 + vec2(-v0.y, v0.x) * orthAdd;
    return uv;
}

vec2 plasma(vec2 uv, float time)
{
    float freq = 1.0f;
    float phase = time;
    float amplitude = 1.0f;
    float orthAdd = 0.3f;

    uv = plasmaIter(uv, freq, phase, amplitude, orthAdd);

    for(int i=0; i<5; i++)
    {
        phase += 0.1f*time;
        freq += 2.0f;    
        amplitude *= 0.5f;

        uv = plasmaIter(uv, freq, phase, amplitude, orthAdd);
    }

    return uv;
}

vec4 colourPlasma(vec2 p, float time)
{
    float r=sin(p.x-time)*0.5+0.5;
    float b=sin(p.y+time)*0.5+0.5;
    float g=sin(p.x+p.y)*0.5+0.5;
	return vec4(r,g,b,1.0); 
}

vec4 smoke(vec2 p)
{
    p = p * p * p * p;
    return vec4(p.x, p.x, p.x, 1.0f);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time=iTime*1.0;
	vec2 uv = (fragCoord.xy / iResolution.xx) * 2.0 - 1.0;
    vec2 p = plasma(uv*6.28, time);
    fragColor = smoke(p);
    //fragColor = colourPlasma(p, time);
}
