// 260 chars
void mainImage (out vec4 F, in vec2 R)
{
    vec2 uv = (2.*R-iResolution.xy)/iResolution.y*5.;
    uv*=mat2(cos(0.7867*(floor(length(floor(uv))))-vec4(0,33,11,0)));
    uv=fract(uv)-.5;
    float d = uv.x+abs(uv.y)+floor(sin(iTime*1.5)*3.)/15.;
    F += (abs(d)-0.15)*16.*vec4(1.,0.2,0.11,1.);
}
