void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/max(iResolution.x, iResolution.y);

    float alpha = atan(uv.y, uv.x);

    float iTime2=iTime/2.;
    float a1=1.0*(1.0+cos(iTime2));
    float a2=0.9*(1.0+cos(iTime2*3./5.0));
    float a3=8.0*(1.0+cos(iTime2*4./7.));
    float b1=1.0*(1.0+cos(iTime2*3.));
    float b2=0.1*(1.0+cos(iTime2*5./7.));
    float b3=2.0*(1.0+cos(iTime2*1./3.));
    float c1=0.9*(1.0+cos(iTime2*1./5.));
    float c2=0.05*(1.0+cos(iTime2*4./3.));
    float c3=20.0;
    float d1=1.0*(1.0+cos(iTime2*4./3.));
    float d2=1.0*(1.0+cos(iTime2));
    float d3=1.0;

    float r=
        (a1+a2*cos(a3*alpha))*
        (b1+b2*cos(b3*alpha))*
        (c1+c2*cos(c3*alpha))*
        (d1+d2*sin(d3*alpha))+(sin(iTime));
    float x=cos(alpha)*r;
    float y=sin(alpha)*r;

    float d=distance(uv, vec2(x,y)/10.)/10.0;

    vec3 color = texture( iChannel0, vec2(d, 0.0)).xyz;
    color = texture( iChannel1, vec2(color.x/20., 0.0)).xyz;

	fragColor = vec4(color, 1.0);
}
