void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = fragCoord/iResolution.xy;

  // use the Green and Blue channel of the brick image
  // to represent a height and width offset
  // giving the illusion of a surface texture
  vec2 offset = texture(iChannel0, uv).gb;

  // multiply the offset to make the effect more or less
  offset = (offset-0.5)*(sin(iTime) + 1.0)*0.2;

  // get the image from with the offset
  // i.e. not from uv, but from uv+offset
  vec3 img = texture(iChannel1, uv+offset).rgb;

  fragColor = vec4(img,1.0);
}
