void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    float radius = .3;
    vec2 center = vec2( mod(iTime/5., 2.0), -0.5 + mod(iTime/5., 2.0));
    uv.x *= iResolution.x/iResolution.y;

    // I have no clue what shape I made but it looks really cool
    uv.x += .1*sin(20.*uv.y + iTime);
    uv.y += .1*cos(13.*uv.x + iTime);

    if(length(uv-center) < radius){
        col = texture(iChannel1, uv).rgb;
        col *= 3.;
        col += vec3(.6*sin(13.*sin(uv.x/2.)), 0.05, .6*sin(9.*sin(uv.x/2.)));
    }

    // Output to screen
    fragColor = vec4(col, 1.);
}
