    bool circle(vec2 uv, vec2 o , float r)
{
    float scaleY = iResolution.y/iResolution.x;
    return (uv.x - o.x)*(uv.x - o.x) + (uv.y - o.y)*(uv.y - o.y)* (scaleY * scaleY) < r*r; 
}

