vec2 complexSquare(vec2 v) {
  return vec2(v.x*v.x-v.y*v.y, 2.0*v.x*v.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - vec2(-0.5, 0.5)) / iResolution.x * 50.0/(exp(iTime)+50.0);
    vec2 z = vec2(0,0);
    int maxIt = 500;
    int i = 0;

    for(; i < maxIt && length(z) < 1.0; i++) {
        z = complexSquare(z) + uv;
    }

    float progress = float(i) / float(maxIt);

    vec3 col = vec3(sqrt(progress),progress,0);

    fragColor = vec4(col,1.0);
}
