vec2 rot(vec2 uv,float a){
	return vec2(uv.x*cos(a)-uv.y*sin(a),uv.y*cos(a)+uv.x*sin(a));
}

float draw(vec2 uv) {
    return clamp(10.0 * (length(uv - vec2(0.75, 0.5))-0.15), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2(fragCoord.x/iResolution.y - 0.5, fragCoord.y/iResolution.y - 0.5);

    float s=1.3;
    float c = draw(uv);
    for(int i=0;i<300;i++){
		uv=uv * 1.04;
		uv=rot(uv, iTime / 100.0);
		//s=s/2.1;
        c = min(c, draw(uv));
	}

    // Output to screen
    fragColor = vec4(c, c, c, 1.0);

}
