#define SHINE 1.08
#define THRESHOLD 0.45
#define WATERSPEED 0.09

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float scroll = iTime * WATERSPEED;
    vec2 scale = vec2(uv.x * 0.8, uv.y * 1.7);
    float offset = 0.2;

    float col1 = texture(iChannel0, scale + vec2(scroll, 0.0)).r;
    float col2 = texture(iChannel0, scale + vec2(-scroll + offset, offset)).r;

    float soup = col1 + col2;
    if (soup <= SHINE) {
        soup = clamp(soup, 0.0, THRESHOLD);
    }
    soup = min(soup, 1.0);

    fragColor = vec4(vec3(soup), 1.0);
}
