void mainImage( out vec4 color, in vec2 o ) {
    int z=1*2*3*4;
    float t=((iTime>180.0)?180.0:iTime);
    int y=int(o.y)-int(iResolution.y)/2;
    int x=int(o.x)+int(iResolution.x*t/float(z));
    color = vec4(
        sin(float((x*x+y*y)&(3*z))),
        sin(float((x*x+y*y)&(2*z))),
        sin(float((x*x+y*y)&(1*z))),
    0.0);
}

vec2 mainSound( int samp, float time ) {
    float t=3.0*time;
    float n=40.0*(mod(floor(t)*floor(t*2.0),32.0)+1.0);
    float v=1.0-mod(t*2.0,1.0);
    return vec2(sin(6.2831*n*t)*v);
}
