#define PI 3.14159265359

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    vec4 baseImage = texture(iChannel0,uv);
    vec4 overlayImage1 = texture(iChannel2,uv);

    uv -= vec2(0.5);
    uv *= rotate2d(PI*2.0/3.0);
    uv += vec2(0.5);

    vec4 overlayImage2 = texture(iChannel2,uv);

    fragColor = (baseImage + overlayImage1) * (baseImage + overlayImage2);
}
