vec2 rot(vec2 v, float a) {
    float s = sin(a);
	float c = cos(a);
	return mat2(c, -s, s, c) * v;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0 * (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    float t = iTime;

    uv = rot(uv, t);
    uv = vec2(sin(atan(uv.x, uv.y) * 7.0) + length(uv) * sin(t * 0.3) * 4.0, 4.0 / length(uv));
    float l = sin(length(uv) - t);
    float c = cos((atan(uv.y, uv.x) * 7.0)) * sin(l * 7.0);
    float l4 = pow(l, 4.0);
    fragColor = vec4(vec3(c * l4, c * l, -c * l4) * 2.0, 1.0);
}
