void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec4 color = vec4(0.5,  0.5,   1.0,  1.0);
    vec4 delta = vec4(0.47, 0.47, -0.34, 0.0);

    float r = 0.3;
    float normD = distance(iResolution.xy / 2.0, fragCoord) / (iResolution.x / 2.0);
    float k = floor(normD  + 1.0 - r);

    fragColor = k * delta + color;
}
