float vsm(float p,float ix,float nx){
    float f=pow(1.0-pow(2.0*abs(fract(p)-0.5),ix),nx);
    float i=step(fract(p),0.5)*2.0;
    return 1.0-max(i-f,f)/2.0;
}
void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    float pi=3.14159265359;
    float scale=2.0;
    float px=scale/iResolution.y;
    vec2 uv=(fragCoord-vec2(0.0,iResolution.y/2.0))/(iResolution.y/scale);

    float t1=iTime*0.5;
    float t2=pow(0.5-cos(0.5*pi*iTime)*0.5,12.0);
    float t3=pow(0.5-cos(0.5*pi*iTime+pi)*0.5,12.0);
    vec3 col;
    float curve=abs(uv.y+0.5-vsm(2.0*(1.0-abs(fract(0.5*uv.x+t1)-0.5)),1.0+t2*80.0,1.0+t3*80.0));
    col+=smoothstep(3.0*px,0.0,curve);
    // Output to screen
    fragColor=vec4(col,1.0);
}
