void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float col = 1.0;
    float minLineThickness = 0.1;
    float speed = 3.0;
    float pulseWidth = 200.;

    float b = cos(iTime*speed+fragCoord.x/pulseWidth)*0.05+minLineThickness;

    col *= smoothstep(b, 0.00, abs(uv.y));
    fragColor = vec4(.0, col, 0.0,1.0);
}
