#define S(x) sign(x)
#define s(v) smoothstep(.1,0.,v)
#define C /255.
#define m3(x) int(mod((x),3.)*2.)
void mainImage(out vec4 O, in vec2 E){
    vec2 u = (E - iResolution.xy * .5 ) / iResolution.y;
    const float K = sqrt(3.), a = .1, h = a * K / 2., h2 = 2. * h, b = 1.0472;
    const vec3[6] cs = vec3[6](vec3(255. C,248. C,210. C),vec3(216. C,255. C,136. C),
                               vec3(107. C,235. C,131. C),vec3(180. C,255. C,195. C),
                               vec3( 95. C,234. C,155. C),vec3( 81. C,162. C,126. C));
    vec3 cl = vec3(1.);
    for(float i = 0.; i < 6.; i++) {
        float X = u.x, Y = u.y,
              L1 = Y+K*X, L2 = Y-K*X, L3 = Y+K*(X+a),
              L4 = -Y+h2, L5 = X+2.*a, L6 = Y-K*L5, L7 = Y-h;
        cl = mix(cl,mix(cs[m3(i)],cs[m3(i)+1],Y/h),
            s(S(3.5-S(Y)-S(-Y+h)-S(-L1)-S(L3))));
        cl = mix(cl,mix(cs[m3(i+1.)],cs[m3(i+1.)+1],L7/h2),
            s(S(2.5-S(L7)-S(Y-K*(X+a))-S(L4)-S(-L6))) + 
            s(S(1.5-S(-L4)-S(-Y-K*(X-1.5*a))-S(3.*h-Y+K*X/3.))));
        cl = mix(cl,mix(cs[m3(i)],cs[m3(i)+1],(-Y+h2)/(.5*h)),
            s(S(2.5-S(Y+K*(X+a*.5))-S(L6)-S(L4))));
        u *= mat2(cos(b),sin(b),-sin(b),cos(b));
    }
    O = vec4(cl,1.0);
}
