    vec3 Triangle( vec3 p, vec3 a, vec3 b, vec3 c){
                 vec3 ab = a - b;
                 vec3 bc = b - c;
                 vec3 ca = c - a;

                 vec3 ap = a - p;
                 vec3 bp = b - p;
                 vec3 cp = c - p;

                  bool b1 = cross(ab, ap).z >= 0.0;
                  bool b2 = cross(bc, bp).z >= 0.0;
                  bool b3 = cross(ca, cp).z >= 0.0;

                   if(b1 && b2 && b3){
                      return vec3(.85, .44, .34);
                   }else{
                      return vec3(0.0);
                   }
    }

    bool Circle(vec2 f, vec2 p, float r) {
               float uv = iResolution.y/iResolution.x;
                return (f.x-p.x)*(f.x-p.x) + (f.y - p.y)*(f.y - p.y)*(uv*uv) < r*r;
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
          uv -= .5;
           uv.x *= -1.;

    //general color
    vec3 col = vec3(0.);

    //head
           if (Circle(uv, vec2(0., -.1), .15)) {
              col = vec3(1., .9, .8);
           }

    //cheek
           if (Circle(uv, vec2(.04,-.16), .05)) {
              col = vec3(1., .65, .6);
           }

    //eye1
           if (Circle(uv, vec2(.06, .02), .03)) {
              col = vec3(0.);
           }

    //eye2
           if (Circle(uv, vec2(.06, .05), .04)) {
              col = vec3(1., .9, .8);
           }

    //mouth
           if (uv.x > .09 && uv.x < .14 && uv.y < -.19 && uv.y > -.2) {
              col = vec3(.85, .44, .34);
           }

    //main hat
           if (uv.x > -.16 && uv.x < .16 && uv.y > .08 && uv.y < .17) {
              col = vec3(1., 0., 0.);
           }

    //middle hat
    //vec2 uv2 = (2.*fragCoord-iResolution.xy)/min(iResolution.x,iResolution.y);
    vec3 v0 = vec3(0., 0.32, 0.0);
    vec3 v1 = vec3( -0.15, .17, 0.0);
    vec3 v2 = vec3( 0.15,  .17, 0.0);
     vec3 res = Triangle(vec3(uv, 0.0), v0, v1, v2);
      col += vec3(res);

    //upper hat
           if (Circle(uv, vec2(.0,.35), .03)) {
              col = vec3(0., 1., 0.);
           }

    //nose 
    //vec2 uv3 = (2.*fragCoord-iResolution.xy)/min(iResolution.x,iResolution.y);
    vec3 v01 = vec3(0.4, -.05, 0.0);
    vec3 v11 = vec3( 0.14, -.05, 0.0);
    vec3 v21 = vec3( 0.14, -.12, 0.0);
     vec3 res2 = Triangle(vec3(uv, 0.0), v01, v11, v21);
      col += vec3(res2);

       fragColor = vec4(col, 1.0);
}
