const vec3 col1 = vec3(0.553,0.933,0.090);
const vec3 col2 = vec3(0.153,0.118,0.643);


vec3 blend(vec3 a, vec3 b, float t){
    vec3 delta = a - b;
    float maxDelta =     max(
        max(
            abs(delta.x),
            abs(delta.y)),
            abs(delta.z)
    );
    vec3 s = sign(delta);
    vec3 c = a - s*maxDelta*t;

    vec3 minc = vec3(
        min(a.x, b.x),
        min(a.y, b.y),
        min(a.z, b.z)
    );

    vec3 maxc = vec3(
        max(a.x, b.x),
        max(a.y, b.y),
        max(a.z, b.z)
    );

    return clamp(c,minc,maxc);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;
    if(uv.y > 0.5)col = mix(col1, col2, uv.x);
    else          col = blend(col1, col2, uv.x);

    fragColor = vec4(col,1.0);
}
