// snowflake from https://shadertoy.com/view/7sdcz4

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U =   ( u+u - R ) / R.y;
    float t = iTime, w = 3./R.y;

    t = mod(iTime,3.1416);
 // U *= 1.- ( 2.*smoothstep(3.1416,0.,t) + 1. ) / 6.; // zoom             
    U *= 1.-  .5* cos(1.608*t);                        // zoom ( 2*pi-acos(1/3) )/pi          
    U *= mat2(cos(.5*t+vec4(0,11,33,0)));              // rot
    U.x = abs(U.x)-1.1+.1*cos(2.*t);                   // move close or appart

    O -= O;                                            // draw the snowflake
    for( int i = 0; i < 6; i++  )
        U = length(U) * cos( (mod( 6.*atan(U.y,U.x)+3.14, 6.28)-3.14)/6. +vec2(0,1.57) ),
        O = max(O, smoothstep( w, -w, U.x+1.73*abs(U.y) - 1. )), // * (i<5? 1.: t/3.1416),
        U *= 3., U.x -= 2., w *= 3.;

    if (sin(iTime)<0.) O = 1.-O;             
    O = sqrt(O);                                       // to sRGB
}
