void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv*2. - vec2(1.,1.))/(vec2(iResolution.x/iResolution.y)) + vec2(sin(iTime)/4.,0.);
    float value = 0.;
    if(uv.y > 0.)
    value = tan(3.1415*(sin(uv.x/uv.y-sin(iTime/8.)*8.)+1.))+0.5;
    else
    value = tan(3.1415*(sin(uv.x/uv.y+sin(iTime/8.)*8.)+1.))+0.5;
    vec3 col = vec3(value/(sin(iTime)+1.), value/(sin(iTime/2.)+1.),value/(sin(iTime/4.)+1.));
    fragColor = vec4(col,1.0);
}
