float func(vec2 uv)
{

//uv = 1.05 * uv + 0.95 *  floor(99.*uv) / 99.;
vec2 dir = uv - .5;
float theta = atan(dir.y,dir.x);
float d = (1.25 + .25 * cos( 0.4 * iTime + 3.14159)) * length(dir) +  0.1 * cos(30. *( theta + 0.2 * iTime));
float val = 0.2 + 0.2 * cos(1. *theta -0.2 * iTime);
val = smoothstep(d,val,2. * val);
return smoothstep(d,
smoothstep(d,0.5 * val,2. * val)
,val + 0.01);


}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.y - vec2(0.375,0.);

   // Normalized pixel coordinates (from 0 to 1)
    float t = 0.2 * iTime;
    //uv *= (1. + 0.075 * cos(uv.y  + t));
    vec2 c = vec2(cos(t),sin(t));
    vec3 col =  vec3(func(uv));
    float b = cos(t) * 0.00125;
    for (int i = 0; i < 6; i++)
    {
    col = abs(0.5 * col - float(i) * vec3(
    func(uv + (b + 0.08375) * float(i) * c),
    func(uv + 0.08 * float(i) * c),
    func(uv + (0.00125 - b + 0.07625) * float(i) * c)
    ));
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
