void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = floor(fragCoord/8.0 + iTime*8.0);
    int modulo = (int(iTime*0.125)+3)%20;
    float val = 1.0 - (float((int(xy.x)^int(xy.y))%modulo))/float(modulo);
    vec4 tex = texture(iChannel0, vec2(val/float(modulo)));
    fragColor = vec4(tex*val);
}
