// Fork of "isolined Britney" by FabriceNeyret2. https://shadertoy.com/view/7sXXRf
// 2021-04-20 21:28:58

// variant of https://shadertoy.com/view/sdlXRj
// using texture instead of noise https://shadertoy.com/view/NdXXRj

#define draw(v,d)       clamp(1. - abs( fract(v-.5) - .5 ) / (d), 0.,1.)
#define Mix(c0,c1,v)    mix(c0,c1,v)
//#define Mix(c0,c1,v)  vec4(c0,c1,0,0) // debug 
#define func( P )       texture(iChannel0, u/R , 2. ).r             \
                        + .001*texture(iChannel1, u/R , 0. ).r     // to cancel overcompressed mpeg regions

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, eps = vec2(1e-3,0),
         U = u / R.y;	
    vec4 T = texture(iChannel2, u/R );
    O-=O;
	float f = func(P),
    l = exp2(floor(log2(.5/22./fwidth(f)))),                       // subvid amount (absolute)
    dl =     fract(log2(.5/22./fwidth(f))),

 // f *= U.x;                                                      // draw isolines 
    df = fwidth(f);
    l *= 12.;
# if 1
     O += Mix( draw(    f*l,    l*df ),
               draw( 2.*f*l, 2.*l*df ),
               dl ) * T;

# else                                                             // color on different layers
#   define Draw(c,ofs)                         \
      O.c = mix( draw(    f*l-ofs,    l*df ),  \
                 draw( 2.*f*l-ofs, 2.*l*df ),  \
                 dl ) * T.c
    float ofs = .33;   // 0. : too subtle to see.
    Draw(r, 0.);
    Draw(g, ofs);
    Draw(b, -ofs);
# endif

    O = sqrt(O);                                                   // to sRGB
}
