#define RED vec3(0.898, 0.004, 0.004)
#define ORANGE vec3(1., 0.553, 0.004)
#define YELLOW vec3(1., 0.933, 0.004)
#define GREEN vec3(0., 0.506, 0.133)
#define BLUE vec3(0., 0.298, 1.)
#define VIOLET vec3(0.459, 0.004, 0.533)
#define PINK vec3(0.961, 0.392, 0.710)
#define TURQUOISE vec3(0.004,0.753,0.753)
#define INDIGO vec3(0.251,0.,0.596)
#define BLACK vec3(0.0, 0.0, 0.0)
#define BROWN vec3(0.447, 0.318, 0.114)


const vec3 RAINBOW[] = vec3[](RED, ORANGE, YELLOW, GREEN, BLUE, VIOLET);
const vec3 ORIGINAL[] = vec3[](PINK, RED, ORANGE, YELLOW, GREEN, TURQUOISE, INDIGO, VIOLET);
const vec3 PHILADELPHIA[] = vec3[](BLACK, BROWN, RED, ORANGE, YELLOW, GREEN, BLUE, VIOLET);
const vec3 GB78TO99[] = vec3[](RED, ORANGE, YELLOW, GREEN, TURQUOISE, INDIGO, VIOLET);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 sceneColor = vec4(1.0);
    float bandInterval = 0.0;
    vec3 flagColor = vec3(1.0);


    if (uv.x <= 0.5 && uv.y <= 0.5) {
        uv.x = uv.x * 2.0;
        uv.y = uv.y * 2.0;
        bandInterval = 1.0 / float(RAINBOW.length());;
        for(int i = 0; i < RAINBOW.length(); i++)
        {
            flagColor = mix(flagColor, RAINBOW[i], step(bandInterval * float(i), 1.0 - uv.y));  
        }
    } else if(uv.x > 0.5 && uv.y > 0.5){
        uv.x = (uv.x - 0.5) * 2.0;
        uv.y = (uv.y - 0.5) * 2.0;
        bandInterval = 1.0 / float(GB78TO99.length());;
        for(int i = 0; i < GB78TO99.length(); i++)
        {
            flagColor = mix(flagColor, GB78TO99[i], step(bandInterval * float(i), 1.0 - uv.y));  
        }
    } else if(uv.x <= 0.5 && uv.y > 0.5){
        uv.x = uv.x * 2.0;
        uv.y = (uv.y - 0.5) * 2.0;
        bandInterval = 1.0 / float(ORIGINAL.length());;
        for(int i = 0; i < ORIGINAL.length(); i++)
        {
            flagColor = mix(flagColor, ORIGINAL[i], step(bandInterval * float(i), 1.0 - uv.y));  
        }
    } else {
        uv.x = (uv.x - 0.5) * 2.0;
        uv.y = uv.y * 2.0;
        bandInterval = 1.0 / float(PHILADELPHIA.length());;
        for(int i = 0; i < PHILADELPHIA.length(); i++)
        {
            flagColor = mix(flagColor, PHILADELPHIA[i], step(bandInterval * float(i), 1.0 - uv.y));  
        }
    }


    sceneColor = texture(iChannel0, uv);

    fragColor = mix(vec4(flagColor, 1.0), sceneColor, 0.5);
}
