#define EPS 0.001
#define S(r,a) smoothstep(r+EPS,r-EPS,a);

float circleProj(vec2 uv, vec3 p, float r){
    return S(r/p.z,length(uv-p.xy/(p.z)));
}

vec3 pointList[8] = vec3[8](
    vec3(-0.5,-0.5,1.),
    vec3(-0.5,0.5,1.),
    vec3(0.5,-0.5,1.),
    vec3(0.5,0.5,1.),

    vec3(-0.5,-0.5,2.),
    vec3(-0.5,0.5,2.),
    vec3(0.5,-0.5,2.),
    vec3(0.5,0.5,2.)

);

mat2 rotX(float a){
    float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
    uv*=2.;

    // Time varying pixel color
    vec3 col = vec3(0.9);
    float zMax = 5.;
    float xMax = 5.;
    /*for(float z = 1.; z <= zMax; z++){
        for(float x = -xMax*0.5; x <= xMax*0.5; x++){
            float y = sin(x+z+iTime)*0.3;
            col = mix(col,vec3(0.1),circleProj(uv, vec3(x,y,z),0.1));

        }   
    }*/


    for(int i = 0; i < 8; i++){
        pointList[i].xy *= rotX(iTime*2.);
        pointList[i].z -= 1.5;
        pointList[i].xz *= rotX(iTime*2.);
        pointList[i].z +=1.5;
        col = mix(col,vec3(0.1),circleProj(uv, pointList[i], 0.125));
    }

    // Output to screen

    fragColor = vec4(col,1.0);
}
