float inverse_smoothstep( float y )
{
    return 0.5 - sin(asin(1.0-2.0*y)/3.0);
}

float smoothstep_t( float x )
{
    return x*x*x*(6.0*x*x-15.0*x+10.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float d1=inverse_smoothstep(uv.x);  
    float d2=smoothstep_t(uv.x);

    d1=length(vec2(0.0,d1)-vec2(0.0,uv.y))*200.0;
    vec3 col1=vec3(0.6,0.98,0.22); 
    col1 =col1/d1;


    d2=length(vec2(0.0,d2)-vec2(0.0,uv.y))*100.0;
    vec3 col2=vec3(0.25,0.52,0.86); 
    col2 =col2/d2;


    fragColor = vec4(col1+col2,1.0);
}
