void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float GRID_SIZE = 50.0;
    float RADIUS = 15.0;

    vec2 p = fragCoord - iResolution.xy * 0.5;
    p = mod(p, GRID_SIZE);

    vec2 d = p - vec2(0.5 * GRID_SIZE);
    float len = length(d);

    float spread = 1.0 + (RADIUS * 0.5) * (sin(iTime) + 1.0) * 0.5;
    fragColor = vec4(1.0 - smoothstep(RADIUS - spread, RADIUS + spread, len), 0, 0, 1);
}
