void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec3 rb = vec3(0.0f);
    vec3 ct = texture(iChannel0,uv.xy -vec2(uv.y / sin(iTime * 0.05), uv.x + sin(uv.y * 12.))).rgb;
    vec3 ct2 = texture(iChannel0,uv.xy + vec2(uv.y, uv.x)).rgb;
    vec3 ct3 = texture(iChannel0,uv.xy * vec2(uv.y + 1.5f, uv.x * ct2.g)).rbb;

    vec3 tt = texture(iChannel0, uv).rgb;

    rb = 1.0f - (vec3(1.0f) - tt.rrr);


    vec2 n = vec2(dFdx(rb.z), dFdy(rb.y)) *6.0f;
    vec2 n2 = vec2(uv.x, uv.y);
    float n3 = dot(n,n2 + sin(iTime));
    // Output to screen
    fragColor = vec4(vec3(n - n3, sin(n3)),1.0);
}
