void mainImage( out vec4 N, in vec2 D )
{
    vec2 uv = D/iResolution.xy;
    N=texture(iChannel0,uv);
    N=sqrt(N);
    N-=0.01;
}

//#define buf(c) texture(iChannel0,(co+c)/iResolution.xy)

vec4 d(vec2 c, vec2 co) {
    return texture(iChannel0,(co+c)/iResolution.xy);
}

void mainImage( out vec4 col, in vec2 co )
{
    col = vec4(0);
    if(iFrame==0)col=vec4(0,0,0,1);
    if(iMouse.z>0.)col+=.5/dot(co-iMouse.xy,co-iMouse.xy);
    for(float i=-2.;i<=2.;i++)
    for(float j=-2.;j<=2.;j++){
        col=max(col,exp(-0.01*sqrt(i*i+j*j))*d(vec2(i,j), co));

    }
    col=min(0.*col*1.4,col-1.);
    col+=.997;
}
