vec2 hash12(float t) {
    float x = fract(sin(t*674.31)*432.1);
    float y = fract(sin(t*472.1)*201.9);
    return vec2(x, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 color = vec3(0);

    float t = iTime * 10.;
    float brightness = .0001*(sin(t)+3.);

    float dt = 0.;


    for (float i=200.; i>0.; i--) {
        vec2 dir = hash12(i)-.5;
        float tt = fract(iTime*.3)*10.;
        dt = length(uv-dir*tt);
        color += brightness/dt;
    }

    fragColor = vec4(color,1.0);
}
