float widthX = 1.0;
float drawGraph(vec2 uv){
    float graphY = uv.x/(uv.x+0.2);
    float diffY = abs(uv.y - graphY);

    float graphX = -(0.2*uv.y/(uv.y-1.));
    float diffX = abs(uv.x - graphX)/widthX;

    float diff = min(diffX, diffY);

    float col = smoothstep(0.011, 0.001, diff);
    if(graphY >1. || graphX < 0. || uv.y < 0.0) col = 0.;

    return col;
}
float drawStar(vec2 uv, float score){
    vec2 p = vec2(score, score/(score+0.2));
    float len = length(uv-p);
    return (0.029+(sin(iTime*12.)/2.+0.5)*0.004)/len;
}
float starLen(vec2 uv, float score){
    vec2 p = vec2(score, score/(score+0.2));
    vec2 diff = uv-p;
    diff.x /= widthX;
    return length(diff);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set uv
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.05;
    uv.x *= iResolution.x/iResolution.y;
    uv = uv*1.1;
    uv.x *= widthX;

    float col = drawGraph(uv);

    float hori = mod(uv.y, 0.25);
    col += step(0.247, hori);
    vec3 colV3 = vec3(0.406, 0.851, 0.361)*col;

    vec3 starColor1 = vec3(0.506, 0.851, 0.960);
    float starLen1 = starLen(uv, 0.1);
    float star1 = (0.029+(sin(iTime*12.)/2.+0.5)*0.004)/starLen1;
    colV3 = mix(colV3, starColor1, clamp(star1, 0., 1.));

    vec3 starColor2 = vec3(.9, 0.496, 0.743);
    float starLen2 = starLen(uv, 0.9);
    float star2 = 0.02/starLen2;
    colV3 = mix(colV3, starColor2, clamp(star2, 0., 1.));


    fragColor = vec4(colV3,1.0);
}
