#define BLUR_LENGTH 100
#define BLUR_STEPS 50

float hash(vec2 co)
{
    return fract(sin(dot(co.xy ,vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float v = fragCoord.y / iResolution.y;
    float rand = hash(fragCoord);

    float epsilon = 0.01;
    float baseIntensity = pow(epsilon, 1./float(BLUR_STEPS));

    vec3 color = vec3(0.);

    for (int i = 0; i < BLUR_STEPS; ++i)
    {
        float offset = (float(i) + rand) * float(BLUR_LENGTH) / float(BLUR_STEPS);

        float u = (fragCoord.x - offset) / iResolution.x;

        float intensity = exp(-float(i) * 5. / offset);
        color += texture(iChannel1, vec2(u, v)).rgb * pow(baseIntensity, float(i));
    }
    color *= 0.3*float(BLUR_LENGTH)/float(BLUR_STEPS);

    fragColor = vec4(color, 1.0);
}

//
// Input image, compositing a video with a static texture just so it looks more interesting.
//

vec3 rgb2hsv(vec3 rgb)
{
	float Cmax = max(rgb.r, max(rgb.g, rgb.b));
	float Cmin = min(rgb.r, min(rgb.g, rgb.b));
    float delta = Cmax - Cmin;

	vec3 hsv = vec3(0., 0., Cmax);

	if (Cmax > Cmin)
	{
		hsv.y = delta / Cmax;

		if (rgb.r == Cmax)
			hsv.x = (rgb.g - rgb.b) / delta;
		else
		{
			if (rgb.g == Cmax)
				hsv.x = 2. + (rgb.b - rgb.r) / delta;
			else
				hsv.x = 4. + (rgb.r - rgb.g) / delta;
		}
		hsv.x = fract(hsv.x / 6.);
	}
	return hsv;
}

float chromaKey(vec3 color)
{
	vec3 backgroundColor = vec3(0.157, 0.576, 0.129);
	vec3 weights = vec3(4., 1., 2.);

	vec3 hsv = rgb2hsv(color);
	vec3 target = rgb2hsv(backgroundColor);
	float dist = length(weights * (target - hsv));
	return 1. - clamp(3. * dist - 1.5, 0., 1.);
}

vec3 changeSaturation(vec3 color, float saturation)
{
	float luma = dot(vec3(0.213, 0.715, 0.072) * color, vec3(1.));
	return mix(vec3(luma), color, saturation);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec3 color = texture(iChannel0, uv).rgb;
	vec3 bg = texture(iChannel1, uv).rgb;

	float incrustation = chromaKey(color);

	color = changeSaturation(color, 0.5);
	color = mix(color, bg, incrustation);

    //color = vec3(smoothstep(0.01, 0.009, length(uv*vec2(iResolution.x/iResolution.y, 1.)-0.5)));
    //color = debugPattern(fragCoord, iResolution.xy);
	fragColor = vec4(color, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution = floor(iResolution.xy / float(PIXEL_SIZE));

    vec2 pixel = fract(fragCoord / float(PIXEL_SIZE));
    vec3 mask = (pixel.x - vec3(0., 2., 4.)/6.) * 3.;
    mask = 1. - clamp(pow(abs(1. - 2. * mask), vec3(8.)), 0., 1.);
    mask *= 1. - clamp(pow(abs(1. - 2. * pixel.y), 3.), 0., 1.);


    vec2 uv = floor(fragCoord/vec2(PIXEL_SIZE))/resolution;
    vec3 color = texture(iChannel0, uv).rgb;
    color = floor(color * COLOR_DEPTH) / COLOR_DEPTH;
    color *= mask*mask;



    fragColor = vec4(color, 1.0);
}
