// Original shader: https://www.shadertoy.com/view/3tdSDj

float udSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 ba = b-a;
    vec2 pa = p-a;
    float h =clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length(pa-h*ba);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = vec2(fragCoord.x, iResolution.y - fragCoord.y);

    vec2 v2 = vec2(30,30);
    vec2 v1 = vec2(iMouse.x, iResolution.y - iMouse.y);
    float thick = 20.;

	float d = udSegment( p, v1, v2 ) - thick;

    vec3 col = vec3(1.0,0,0) * smoothstep(1.5,0.,d);

	fragColor = vec4(col,1.0);
}
