void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    const vec2 rep_each = vec2(0.07, 0.07);
    const float line_r = 0.1;
    const float curve = 2.0;

    float li = mod(floor(uv.y / rep_each.y), 2.0);

    float re = abs(mod(floor(uv.x / rep_each.x), 2.0) - li);
    float le = 1.0 - re;

    float lv = exp(-curve * mod(uv.x, rep_each.x) / rep_each.x) * rep_each.y;
    float rv = exp(curve * (mod(uv.x, rep_each.x) / rep_each.x - 1.0)) * rep_each.y;

    float d = mod(uv.y, rep_each.y) - (lv * le + rv * re);
    float be = 1.0 - clamp(ceil(abs(d / rep_each.y) - line_r), 0.0, 1.0);

    vec3 c1 = vec3(0.17, 0.6, 0.23);
    vec3 c2 = vec3(0.11, 0.27, 0.14);

    fragColor = vec4(mix(c1, c2, be), 1.0);
}
